import React, { useState } from 'react';
import './App.css';

const gradePoints = {
  'A++': 10, 'A+': 10, 'A': 9, 'B+': 8, 'B': 7, 'C+': 6, 'C': 5, 'D+': 4.5, 'D': 4, 'E+': 3, 'F': 0
};

const gradeOptions = [
  { label: 'A++ (10)', value: 'A++' },
  { label: 'A+ (10)', value: 'A+' },
  { label: 'A (9)', value: 'A' },
  { label: 'B+ (8)', value: 'B+' },
  { label: 'B (7)', value: 'B' },
  { label: 'C+ (6)', value: 'C+' },
  { label: 'C (5)', value: 'C' },
  { label: 'D+ (4.5)', value: 'D+' },
  { label: 'D (4)', value: 'D' },
  { label: 'E+ (3)', value: 'E+' },
  { label: 'F (0)', value: 'F' },
];

function App() {
  const [activeTab, setActiveTab] = useState('sgpa');
  const [subjects, setSubjects] = useState([{ id: 1, name: '', grade: '', credits: '' }]);
  const [semesters, setSemesters] = useState([{ id: 1, semester: '', sgpa: '', credits: '' }]);
  const [result, setResult] = useState(null);
  const [showHelp, setShowHelp] = useState(false);
  const [isCalculating, setIsCalculating] = useState(false);

  // Subject handlers
  const addSubject = () => setSubjects([...subjects, { id: Date.now(), name: '', grade: '', credits: '' }]);
  const removeSubject = id => subjects.length > 1 && setSubjects(subjects.filter(s => s.id !== id));
  const updateSubject = (id, field, value) => setSubjects(subjects.map(s => s.id === id ? { ...s, [field]: value } : s));

  // Semester handlers
  const addSemester = () => setSemesters([...semesters, { id: Date.now(), semester: '', sgpa: '', credits: '' }]);
  const removeSemester = id => semesters.length > 1 && setSemesters(semesters.filter(s => s.id !== id));
  const updateSemester = (id, field, value) => setSemesters(semesters.map(s => s.id === id ? { ...s, [field]: value } : s));

  // Calculation logic
  const calculateSGPA = () => {
    setIsCalculating(true);
    setTimeout(() => {
      let totalCredits = 0, totalGradePoints = 0, subjectCount = 0, validSubjects = 0;
      for (const subject of subjects) {
        if (subject.grade) {
          const gradePoint = gradePoints[subject.grade];
          if (gradePoint !== undefined) {
            validSubjects++;
            if (subject.credits && subject.credits.trim() !== '') {
              const credits = parseFloat(subject.credits);
              if (credits > 0) {
                totalCredits += credits;
                totalGradePoints += gradePoint * credits;
              }
            } else {
              totalGradePoints += gradePoint;
              subjectCount++;
            }
          }
        }
      }
      if (validSubjects === 0) {
        setResult({ type: 'error', message: 'Please enter at least one valid grade' });
        setIsCalculating(false);
        return;
      }
      let sgpa;
      if (totalCredits > 0) {
        sgpa = totalGradePoints / totalCredits;
        setResult({ type: 'sgpa', value: sgpa.toFixed(2), totalCredits: totalCredits.toFixed(1), calculationType: 'credit-weighted', validSubjects });
      } else {
        sgpa = totalGradePoints / subjectCount;
        setResult({ type: 'sgpa', value: sgpa.toFixed(2), totalCredits: `${subjectCount} subjects`, calculationType: 'simple-average', validSubjects });
      }
      setIsCalculating(false);
    }, 600);
  };

  const calculateCGPA = () => {
    setIsCalculating(true);
    setTimeout(() => {
      let totalCredits = 0, totalGradePoints = 0, validSemesters = 0;
      for (const semester of semesters) {
        if (semester.sgpa && semester.credits) {
          const credits = parseFloat(semester.credits);
          const sgpa = parseFloat(semester.sgpa);
          if (sgpa >= 0 && sgpa <= 10 && credits > 0) {
            totalCredits += credits;
            totalGradePoints += sgpa * credits;
            validSemesters++;
          }
        }
      }
      if (validSemesters === 0) {
        setResult({ type: 'error', message: 'Please enter valid SGPA and credits for at least one semester' });
        setIsCalculating(false);
        return;
      }
      const cgpa = totalGradePoints / totalCredits;
      setResult({ type: 'cgpa', value: cgpa.toFixed(2), totalCredits: totalCredits.toFixed(1), validSemesters });
      setIsCalculating(false);
    }, 600);
  };

  const resetCalculator = () => {
    if (activeTab === 'sgpa') {
      setSubjects([{ id: 1, name: '', grade: '', credits: '' }]);
    } else {
      setSemesters([{ id: 1, semester: '', sgpa: '', credits: '' }]);
    }
    setResult(null);
  };

  const getGradeLevel = value => {
    const val = parseFloat(value);
    if (val >= 9.5) return 'Outstanding';
    if (val >= 9) return 'Excellent';
    if (val >= 8) return 'Very Good';
    if (val >= 7) return 'Good';
    if (val >= 6) return 'Satisfactory';
    return 'Improvement Needed';
  };

  // Icon fallback
  const LucideIcon = ({ name, ...props }) => {
    // Use Lucide icons if available, else fallback to emoji/text
    const icons = {
      Plus: '+',
      Trash2: '🗑️',
      Calculator: '🧮',
      BookOpen: '📖',
      Award: '🏆',
      Info: 'ℹ️',
      RefreshCw: '⟳',
      Star: '★',
    };
    if (window.lucide && window.lucide[name]) {
      const Icon = window.lucide[name];
      return <Icon {...props} />;
    }
    return <span {...props}>{icons[name] || '?'}</span>;
  };

  return (
    <div className="app-root">
      <div className="header">
        <div className="header-icon"><LucideIcon name="Award" style={{ fontSize: 24 }} /></div>
        <h1>ACADCALC</h1>
        <p className="subtitle">Precision Academic Performance Calculator</p>
        <button className="help-btn" onClick={() => setShowHelp(!showHelp)}>
          <LucideIcon name="Info" style={{ fontSize: 16, marginRight: 4 }} />
          How to use
        </button>
      </div>
      {showHelp && (
        <div className="help-section">
          <h3>Instructions</h3>
          <div className="help-columns">
            <div>
              <h4>SGPA Calculator</h4>
              <ul>
                <li>Enter subject names and select corresponding grades</li>
                <li>Add credits if your institution uses credit-based system</li>
                <li>Without credits, equal weightage is applied to all subjects</li>
                <li>Grade scale: A++ (10) → A+ (10) → A (9) → B+ (8) → B (7) → C+ (6) → C (5) → D+ (4.5) → D (4) → E+ (3) → F (0)</li>
              </ul>
            </div>
            <div>
              <h4>CGPA Calculator</h4>
              <ul>
                <li>Input semester details with SGPA and credit values</li>
                <li>SGPA must be within 0-10 range</li>
                <li>Credit values determine semester weightage</li>
                <li>All calculations are performed in real-time</li>
              </ul>
            </div>
          </div>
        </div>
      )}
      <div className="tab-nav">
        <button className={activeTab === 'sgpa' ? 'active' : ''} onClick={() => { setActiveTab('sgpa'); setResult(null); }}>
          <LucideIcon name="BookOpen" style={{ fontSize: 18, marginRight: 6 }} /> SGPA Calculator
        </button>
        <button className={activeTab === 'cgpa' ? 'active' : ''} onClick={() => { setActiveTab('cgpa'); setResult(null); }}>
          <LucideIcon name="Calculator" style={{ fontSize: 18, marginRight: 6 }} /> CGPA Calculator
        </button>
      </div>
      {activeTab === 'sgpa' && (
        <div className="panel">
          <h2>SGPA Calculator</h2>
          <div className="subject-list">
            {subjects.map((subject, idx) => (
              <div className="subject-row" key={subject.id}>
                <span className="subject-num">{idx + 1}</span>
                <input
                  type="text"
                  placeholder="Subject Name"
                  value={subject.name}
                  onChange={e => updateSubject(subject.id, 'name', e.target.value)}
                />
                <select
                  value={subject.grade}
                  onChange={e => updateSubject(subject.id, 'grade', e.target.value)}
                >
                  <option value="">Select Grade</option>
                  {gradeOptions.map(opt => (
                    <option key={opt.value} value={opt.value}>{opt.label}</option>
                  ))}
                </select>
                <input
                  type="number"
                  placeholder="Credits (optional)"
                  value={subject.credits}
                  min="0"
                  step="0.5"
                  onChange={e => updateSubject(subject.id, 'credits', e.target.value)}
                />
                <button className="remove-btn" onClick={() => removeSubject(subject.id)} disabled={subjects.length === 1}>
                  <LucideIcon name="Trash2" style={{ fontSize: 16 }} />
                </button>
              </div>
            ))}
          </div>
          <div className="panel-actions">
            <button className="add-btn" onClick={addSubject}>
              <LucideIcon name="Plus" style={{ fontSize: 16, marginRight: 4 }} /> Add Subject
            </button>
            <button className="calc-btn" onClick={calculateSGPA} disabled={isCalculating}>
              {isCalculating ? <LucideIcon name="RefreshCw" className="spin" style={{ fontSize: 16, marginRight: 4 }} /> : <LucideIcon name="Calculator" style={{ fontSize: 16, marginRight: 4 }} />}
              {isCalculating ? 'Calculating...' : 'Calculate SGPA'}
            </button>
            <button className="reset-btn" onClick={resetCalculator}>Reset</button>
          </div>
        </div>
      )}
      {activeTab === 'cgpa' && (
        <div className="panel">
          <h2>CGPA Calculator</h2>
          <div className="subject-list">
            {semesters.map((semester, idx) => (
              <div className="subject-row" key={semester.id}>
                <span className="subject-num">{idx + 1}</span>
                <input
                  type="text"
                  placeholder="Semester Name"
                  value={semester.semester}
                  onChange={e => updateSemester(semester.id, 'semester', e.target.value)}
                />
                <input
                  type="number"
                  placeholder="SGPA (0-10)"
                  value={semester.sgpa}
                  min="0"
                  max="10"
                  step="0.01"
                  onChange={e => updateSemester(semester.id, 'sgpa', e.target.value)}
                />
                <input
                  type="number"
                  placeholder="Credits"
                  value={semester.credits}
                  min="0"
                  step="0.5"
                  onChange={e => updateSemester(semester.id, 'credits', e.target.value)}
                />
                <button className="remove-btn" onClick={() => removeSemester(semester.id)} disabled={semesters.length === 1}>
                  <LucideIcon name="Trash2" style={{ fontSize: 16 }} />
                </button>
              </div>
            ))}
          </div>
          <div className="panel-actions">
            <button className="add-btn" onClick={addSemester}>
              <LucideIcon name="Plus" style={{ fontSize: 16, marginRight: 4 }} /> Add Semester
            </button>
            <button className="calc-btn" onClick={calculateCGPA} disabled={isCalculating}>
              {isCalculating ? <LucideIcon name="RefreshCw" className="spin" style={{ fontSize: 16, marginRight: 4 }} /> : <LucideIcon name="Calculator" style={{ fontSize: 16, marginRight: 4 }} />}
              {isCalculating ? 'Calculating...' : 'Calculate CGPA'}
            </button>
            <button className="reset-btn" onClick={resetCalculator}>Reset</button>
          </div>
        </div>
      )}
      {result && (
        <div className={`result-panel ${result.type === 'error' ? 'error' : ''}`}>
          {result.type === 'error' ? (
            <div className="result-error">
              <span className="error-icon">!</span>
              <span>{result.message}</span>
            </div>
          ) : (
            <div className="result-success">
              <h3>{result.value}</h3>
              <p className="result-type">{result.type === 'sgpa' ? 'SGPA' : 'CGPA'}</p>
              <p className="result-grade">{getGradeLevel(result.value)}</p>
              <div className="result-details">
                <div><span>Total Credits:</span> <span>{result.totalCredits}</span></div>
                <div><span>{result.type === 'sgpa' ? 'Valid Subjects:' : 'Valid Semesters:'}</span> <span>{result.validSubjects || result.validSemesters}</span></div>
                {result.calculationType && (
                  <div><span>Method:</span> <span>{result.calculationType === 'credit-weighted' ? 'Credit-Weighted' : 'Simple Average'}</span></div>
                )}
              </div>
            </div>
          )}
        </div>
      )}
      <div className="footer">
        <p>Data persists within your current session</p>
      </div>
    </div>
  );
}

export default App;
